
var mysql = require('mysql')
var j2x = require('json2xls')
var fs = require('fs')

var con = mysql.createConnection({
    host:'localhost',
    user:'root',
    password:'',
    database:'epicabasedatos'
});

  con.connect(function(err) {
    if (err) throw err;
    console.log("Connected!");

    sqlQuery = 'SELECT * FROM identificador'

    con.query(sqlQuery, function (err, response) {
      if (err) throw err;
      fs.writeFileSync(`${__dirname}/excel/dato.xlsx`, j2x(response), 'binary')
      con.end()
    })
  })